var pto={};pto.TWENTY_FIVE_MINUTES=15e5;pto.countdown=pto.TWENTY_FIVE_MINUTES;pto.countdownCompleteTime=pto.TWENTY_FIVE_MINUTES;pto.date;pto.timer;pto.timerRunning=false;pto.intervalComplete=function(){if(0<pto.countdown){pto.timerStep()}else{pto.timerComplete()}};pto.timerStep=function(){pto.countdown-=1e3;$(".timer-display").text(pto.milliSecondsToTimeFormat(pto.countdown))};pto.timerComplete=function(){clearInterval(pto.timer);var e=$(".task").val();$(".task-container").append("<p>"+e+"</p>")};pto.start=function(){if(!pto.timerRunning){pto.timer=setInterval(function(){pto.intervalComplete()},1e3);pto.timerRunning=true}};pto.pause=function(){clearInterval(pto.timer);pto.timerRunning=false};pto.reset=function(){pto.countdown=pto.countdownCompleteTime;$(".timer-display").text(pto.milliSecondsToTimeFormat(pto.countdown));clearInterval(pto.timer);pto.timerRunning=false};pto.setTimerDuration=function(e){pto.countdown=e;pto.countdownCompleteTime=e;clearInterval(pto.timer);pto.timerRunning=false;$(".timer-display").text(pto.milliSecondsToTimeFormat(pto.countdown))};pto.milliSecondsToTimeFormat=function(e){pto.date=new Date(e);return pto.convertToDoubleDigit(pto.date.getMinutes())+":"+pto.convertToDoubleDigit(pto.date.getSeconds())};pto.convertToDoubleDigit=function(e){if(e.toString().length<2){e="0"+e}return e.toString()}